INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (9, 'Meu Deus do céu!','Depois do lançamento desse jogo eu não consio mais sair de casa', 5, 1);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (10, 'Senti falta do glitch de montaria humana','Senti falta do glitch de montaria humana', 6, 1);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (11, 'Fiquei que nem o Travolta jogando isso.','Fiquei que nem o Travolta jogando isso.', 7, 1);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (13, 'O melhor mundo aberto já feito', 'Cada detalhe parece vivo, e a história do Arthur é perfeita.', 9, 2);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (22, 'Imersão absurda', 'Caçar, pescar, tudo parece tão real que dá vontade de morar no Velho Oeste.', 4, 2);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (23, 'Um final que destrói', 'Nunca um jogo mexeu tanto comigo quanto o final do Arthur.', 7, 2);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (2, 'The Witcher 3 ainda vale a pena!', 'Mesmo depois de anos, continua sendo um dos melhores RPGs.', 2, 3);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (14, 'Gwent é vida', 'Passei mais tempo jogando Gwent do que salvando a Ciri.', 10, 3);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (24, 'Melhor DLC já feita', 'Blood and Wine parece um jogo novo dentro do jogo.', 6, 3);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (15, 'Eu apanhei… e gostei', 'Desafiador, mas quando você vence um chefe a sensação é indescritível.', 4, 4);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (26, 'Genichiro me traumatizou', 'Depois dele, virei outra pessoa. Nunca mais subestimo um boss.', 2, 4);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (27, 'Mobilidade fantástica', 'A prótese dá muita liberdade, tudo flui de forma perfeita.', 9, 4);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (16, 'Foto modo inimaginável', 'Fiquei 2 horas tirando fotos antes mesmo de começar as missões.', 6, 5);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (28, 'Duelo à luz do pôr do sol', 'Cinematográfico demais. Parece filme do Kurosawa.', 10, 5);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (29, 'Tsushima é linda demais', 'Fiquei explorando só para ver as paisagens.', 3, 5);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (17, 'Clássico eterno', 'Esse jogo nunca envelhece, sempre divertido.', 3, 6);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (30, 'Jogo que marcou minha infância', 'O som das moedas me dá nostalgia instantânea.', 5, 6);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (31, 'Yoshi é o melhor companheiro', 'Nunca deixo ele para trás, dói o coração até hoje.', 1, 6);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (18, 'Mapa gigante!', 'O mundo é lindo e assustador ao mesmo tempo. Um metroidvania impecável.', 7, 7);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (32, 'Chefes impecáveis', 'Cada chefe parece uma dança. Precisa de paciência e precisão.', 4, 7);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (33, 'História misteriosa demais', 'Tive que assistir 10 vídeos depois para entender tudo, mas valeu a pena.', 8, 7);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (19, 'Chorei logo na abertura', 'A animação e trilha sonora são simplesmente emocionantes.', 5, 8);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (34, 'Visual de tirar o fôlego', 'Nunca vi um jogo 2D tão bonito.', 6, 8);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (35, 'Jogabilidade precisa', 'Passar pelas seções difíceis dá uma sensação ótima.', 2, 8);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (20, 'Chefes insanos', 'Difícil, mas extremamente satisfatório! Cada chefe é uma obra de arte.', 1, 9);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (36, 'Arte impecável', 'Cada chefe poderia ser um pôster de tão bonito.', 7, 9);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (37, 'Não é fácil, mas é justo', 'Quando você perde, sabe que foi sua culpa. Isso motiva.', 10, 9);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (21, 'Viagem no tempo é genial', 'As versões de passado/presente/futuro deixam tudo muito mais interessante.', 2, 10);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (38, 'Melhor trilha da série', 'As músicas futuristas são muito boas.', 9, 10);
INSERT INTO tb_post (id, title, content, user_id, game_id) VALUES (39, 'Metal Sonic é incrível', 'A corrida contra ele é uma das minhas partes favoritas da franquia.', 3, 10);